var Field = function(model, name) {
    this.model = model;
    this.name = name;
    if(this.initialize instanceof Function) {
        this.initialize.apply(this, Array.prototype.slice.call(arguments, 2));
    }
};

Field.subclass = function(options) {
    var F = function() {
        Field.apply(this, Array.prototype.slice.call(arguments));
    };
    F.prototype = Field.prototype;
    Object.keys(options).forEach(function(key) {
        this[key] = options[key];
    }, F.prototype);
    return F;
};

exports.Field = Field;
