var related = require('wilson/models/fields/related'),
    basic = require('wilson/models/fields/basic'),
    FIELD_GENERATOR_KEY = 'field_type';

exports.FIELD_GENERATOR_KEY = FIELD_GENERATOR_KEY;

Object.keys(related).forEach(function(key) {
    exports[key] = function(to, kwargs) {
        var inner = function(model, name) {
            return new related[key](model, name, to, kwargs);
        };
        inner[FIELD_GENERATOR_KEY] = key;
        return inner;
    };
});

Object.keys(basic).forEach(function(key) {
    exports[key] = function(kwargs) {
        var inner = function(model, name) {
            return new basic[key](model, name, kwargs);
        };
        inner[FIELD_GENERATOR_KEY] = key;
        return inner;
    };
});
